import React, { useEffect, useState } from "react";
import { Pie, Bar, Line, Doughnut } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, PointElement, LineElement } from "chart.js";
import "../Styles/Dashboard.css";

// Register chart elements
ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, PointElement, LineElement);

const Dashboard = () => {
  const [savings, setSavings] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [income, setIncome] = useState([]);

  // Fetch data from db.json
  useEffect(() => {
    fetch("http://localhost:3000/savings")
      .then((res) => res.json())
      .then((data) => setSavings(data));

    fetch("http://localhost:3000/transactions")
      .then((res) => res.json())
      .then((data) => setTransactions(data));

    fetch("http://localhost:3000/income")
      .then((res) => res.json())
      .then((data) => setIncome(data));
  }, []);

  if (!savings || transactions.length === 0 || income.length === 0) {
    return <p className="loading">Loading dashboard...</p>;
  }

  // Pie Chart: Expense Breakdown
  const expenseData = {
    labels: transactions.map((t) => t.category),
    datasets: [
      {
        data: transactions.map((t) => t.amount),
        backgroundColor: ["#ff6384", "#36a2eb", "#ffce56", "#4bc0c0", "#9966ff"],
      },
    ],
  };

  // Bar Chart: Savings vs Goals
  const savingsData = {
    labels: ["Current Savings", "Savings Goal"],
    datasets: [
      {
        label: "Amount ($)",
        data: [savings.current, savings.goal],
        backgroundColor: ["#4caf50", "#ff9800"],
      },
    ],
  };

  // Line Chart: Savings Over Time
  const savingsGrowthData = {
    labels: savings.history.map((entry) => entry.date),
    datasets: [
      {
        label: "Savings Over Time",
        data: savings.history.map((entry) => entry.amount),
        borderColor: "#42A5F5",
        backgroundColor: "rgba(66, 165, 245, 0.2)",
        fill: true,
      },
    ],
  };

  // Doughnut Chart: Income vs Expenses
  const incomeExpenseData = {
    labels: ["Income", "Expenses"],
    datasets: [
      {
        data: [income.total, transactions.reduce((acc, t) => acc + t.amount, 0)],
        backgroundColor: ["#66bb6a", "#ef5350"],
      },
    ],
  };

  return (
    <div className="dashboard-container">
      <h1>Finance Dashboard</h1>

      <div className="charts">
        {/* Expense Breakdown (Pie Chart) */}
        <div className="chart-card">
          <h3>Expense Breakdown</h3>
          <Pie data={expenseData} />
        </div>

        {/* Savings vs Goal (Bar Chart) */}
        <div className="chart-card">
          <h3>Savings vs Goal</h3>
          <Bar data={savingsData} />
        </div>

        {/* Savings Growth Over Time (Line Chart) */}
        <div className="chart-card">
          <h3>Savings Growth Over Time</h3>
          <Line data={savingsGrowthData} />
        </div>

        {/* Income vs Expenses (Doughnut Chart) */}
        <div className="chart-card">
          <h3>Income vs Expenses</h3>
          <Doughnut data={incomeExpenseData} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
