import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../Styles/Home.css";
import aiIcon from "../assets/ai.svg"; // AI Icon
import secureIcon from "../assets/secure.svg"; // Security Icon
import investIcon from "../assets/invest.svg"; // Investment Icon

const Home = () => {
  const [cryptoPrice, setCryptoPrice] = useState(50000); // Mock BTC price

  // Simulate live market price updates
  useEffect(() => {
    const interval = setInterval(() => {
      setCryptoPrice((prev) => (prev + Math.random() * 200 - 100).toFixed(2));
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home-container">
      {/* BACKGROUND ANIMATION */}
      <div className="animated-bg"></div>

      {/* HERO SECTION */}
      <section className="hero">
        <div className="hero-content">
          <h1 className="glide-in">Revolutionizing Finance with AI ðŸš€</h1>
          <p className="glide-in">The future of smart investments is here.</p>
          <Link to="/signup" className="cta-button">Join WiseWealth</Link>
        </div>
      </section>

      {/* LIVE DATA TICKER */}
      <div className="live-ticker">
        <span>ðŸ”¥ Bitcoin Price: ${cryptoPrice} ðŸ”¥</span>
      </div>

      {/* FEATURES SECTION */}
      <section className="features">
        <h2>Why Choose WiseWealth?</h2>
        <div className="feature-slider">
          <div className="feature-card">
            <img src={aiIcon} alt="AI Insights" />
            <h3>AI-Powered Insights</h3>
            <p>Let our AI analyze market trends & grow your wealth automatically.</p>
          </div>
          <div className="feature-card">
            <img src={secureIcon} alt="Security" />
            <h3>Military-Grade Security</h3>
            <p>Your assets are protected with next-gen encryption.</p>
          </div>
          <div className="feature-card">
            <img src={investIcon} alt="Invest Smartly" />
            <h3>Smart Investments</h3>
            <p>Maximize returns with AI-driven strategies & market analysis.</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
