import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Chart from "chart.js/auto";
import "../Styles/Home.css";

const Home = () => {
  const [expenses, setExpenses] = useState([
    { category: "Food", amount: 150 },
    { category: "Travel", amount: 80 },
    { category: "Utilities", amount: 120 },
    { category: "Entertainment", amount: 100 },
  ]);

  const [savingsGoal, setSavingsGoal] = useState(500);
  const [savedAmount, setSavedAmount] = useState(200);

  useEffect(() => {
    // Create Pie Chart for Expenses
    const ctx = document.getElementById("expensesChart");
    if (ctx) {
      new Chart(ctx, {
        type: "pie",
        data: {
          labels: expenses.map((exp) => exp.category),
          datasets: [
            {
              data: expenses.map((exp) => exp.amount),
              backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4CAF50"],
            },
          ],
        },
      });
    }

    // Create Bar Chart for Savings
    const savingsCtx = document.getElementById("savingsChart");
    if (savingsCtx) {
      new Chart(savingsCtx, {
        type: "bar",
        data: {
          labels: ["Goal", "Saved"],
          datasets: [
            {
              data: [savingsGoal, savedAmount],
              backgroundColor: ["#FFCE56", "#4CAF50"],
            },
          ],
        },
      });
    }
  }, [expenses, savingsGoal, savedAmount]);

  return (
    <div className="home-container">
      {/* HERO SECTION */}
      <section className="hero">
        <h1>Take Control of Your Finances</h1>
        <p>Track expenses, set goals, and achieve financial freedom.</p>
        <Link to="/signup" className="cta-button">Start Budgeting</Link>
      </section>

      {/* EXPENSE CHART */}
      <section className="charts">
        <h2>Your Expenses Overview</h2>
        <canvas id="expensesChart"></canvas>
      </section>

      {/* SAVINGS PROGRESS */}
      <section className="savings">
        <h2>Savings Progress</h2>
        <canvas id="savingsChart"></canvas>
      </section>
    </div>
  );
};

export default Home;
