import React, { useEffect, useState } from "react";
import { Pie, Bar } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from "chart.js";
import "../Styles/Dashboard.css";

// Register chart elements
ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const Dashboard = () => {
  const [savings, setSavings] = useState(null);
  const [transactions, setTransactions] = useState([]);

  // Fetch data from db.json
  useEffect(() => {
    fetch("http://localhost:3000/savings")
      .then((res) => res.json())
      .then((data) => setSavings(data));

    fetch("http://localhost:3000/transactions")
      .then((res) => res.json())
      .then((data) => setTransactions(data));
  }, []);

  if (!savings || transactions.length === 0) {
    return <p>Loading dashboard...</p>;
  }

  // Pie Chart Data (Expenses Breakdown)
  const pieData = {
    labels: transactions.map((t) => t.category),
    datasets: [
      {
        data: transactions.map((t) => t.amount),
        backgroundColor: ["#ff6384", "#36a2eb", "#ffce56", "#4bc0c0", "#9966ff"],
      },
    ],
  };

  // Bar Chart Data (Savings vs Goal)
  const barData = {
    labels: ["Current Savings", "Savings Goal"],
    datasets: [
      {
        label: "Amount ($)",
        data: [savings.current, savings.goal],
        backgroundColor: ["#4caf50", "#ff9800"],
      },
    ],
  };

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>

      <div className="charts">
        {/* Pie Chart: Expenses Breakdown */}
        <div className="chart-card">
          <h3>Expense Breakdown</h3>
          <Pie data={pieData} />
        </div>

        {/* Bar Chart: Savings vs Goals */}
        <div className="chart-card">
          <h3>Savings vs Goal</h3>
          <Bar data={barData} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
